import UIKit

struct RacingTrackModel {
    let trackName: String
    let trackLogo: UIImage?
    let configurations: [String]
    let description: String
    
    static let admRaceway = RacingTrackModel(
        trackName: "ADM Raceway",
        trackLogo: UIImage(named: "logoADMRaceway"),
        configurations: ["Sprint cut", "Grade 4"],
        description: "Автодром «Мячково», «ADM-raceway» или «Московское кольцо» — гоночная трасса, расположенная в Раменском районе Московской области, в 20 км от МКАД по Новорязанскому шоссе. Одна из старейших стационарных гоночных трасс в России, находится на территории аэродрома «Мячково». Активно используется для состязаний с 2002 года. Дважды претерпевала полную реконструкцию - в 2005 и в 2017 годах. Трасса имеет множество конфигураций различной протяженности и сложности, в том числе со сменой направления. Максимальная длина около 3300 метров. В зимний период времени на нем также проводятся соревнования и открытые тренировочные сессии. Отлично подходит для самых разных дисциплин и видов техники."
    )
    
    static let smolenskRing = RacingTrackModel(
        trackName: "Смоленское кольцо",
        trackLogo: UIImage(named: "logoSmolenskRing"),
        configurations: ["Смоленское кольцо"],
        description: "Автодром «Смоленское кольцо» или «Smolensk Ring» — кольцевая гоночная трасса в Дорогобужском районе Смоленской области, в поселке Верхнеднепровский. Трасса cпроектирована при участии известного гоночного архитектора Германа Тильке, введена в эксплуатацию летом 2010 года. Ключевой особенностью можно считать расположение полноценного гоночного трека на достаточно небольшом участке земли. Трасса имеет протяженность 3362 метров и ширину от 12 до 15 метров, состоит из 13 поворотов (8 левых и 5 правых) с ощутимыми перепадами высот и только одну гоночную конфигурацию. Данный гоночный трек принимает у себя не только различные этапы российских гоночных серий, но и часть этапов состязаний Республики Беларусь. Максимальная расчетная скорость для автомобилей серии DTM - 241 км/ч."
    )
    
    static let MoscowRaceway = RacingTrackModel(
        trackName: "Moscow Raceway",
        trackLogo: UIImage(named: ""),
